# **BUT IF THERE ARE ARGUMENTS IN THE FUCTION DO THIS 

def greet2(fx):
    def mfx(*args, **kwargs):
        print('good morning')
        fx(*args, **kwargs)
        print('good night')
        
    return mfx

@greet2
def add(a, b):
    print(a + b)
    

add(7, 3)


